import React from 'react';

const QuizCard = ({ quiz, isTeacher, onJoin }) => {
  return (
    <div className="quiz-card">
      <h3>{quiz.title}</h3>
      <p>{quiz.questions.length} soru</p>
      <div className="card-actions">
        {isTeacher ? (
          <>
            <button className="action-btn edit">Düzenle</button>
            <button className="action-btn start">Başlat</button>
          </>
        ) : (
          <button className="action-btn join" onClick={onJoin}>
            Katıl
          </button>
        )}
      </div>
    </div>
  );
};

export default QuizCard;
